import React from 'react';
import './App.css';

class App extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      markdown : ""
    }
    this.handleChange = this.handleChange.bind(this)
  }
  handleChange(event){
    this.setState({markdown: event.target.value})
  }
  render(){
  return (
    <div className="App">
      <header className="App-header">

        <p>
          TEST CHANGE
        </p>


      </header>
      <div className='flex-container'>
        <div className='input-div'><textarea className='input-text' onChange={this.handleChange}></textarea></div>
        <div className='output-div'><div className='output-text' type= "text-area">asdas{this.markdown}</div></div>
      </div>

    </div>
  );
  }
}

export default App;
